mapper.update:
UPDATE TO departments
SET 
	   department_name = #{departmentName}
	  , manager_id = #{managerId}
	  , location_id = #{locationId}

WHERE department_id = #{departmentId}

mapper.insert:
INSERT INTO departments
		(
			department_id
			, department_name

			, manager_id

			, location_id
		)
VALUES (
		 #{departmentId}
		 , #{departmentName}

		 , #{managerId}

		 , #{locationId}
		)
